import React, { useState, useEffect } from 'react';
import firebase from '../firebase';

type Props = {
    id: string,
    titre?: string,
    photo?: string
}

const Projet: React.FC<Props> = ({ id, titre, photo }) => {
    const [editTitre, setEditTitre] = useState<boolean>(false);

    useEffect(() => {
        // setTimeout(function() {
            document.getElementById(id)?.classList.add('ouvert');
        // }, 1000);
    }, [id]);

    const handleDelete = () => {
        firebase.firestore().collection('œuvres').doc(id).delete();
    }

    return(
        <div className="projet" id={id} style={{backgroundImage: `url(${photo})`}}>
            <div className="edit">
                {editTitre === false && <h4 onClick={() => setEditTitre(true)}>{titre}</h4>}
                {editTitre === true && 
                    <form>
                        <input autoFocus type="text" />
                    </form>
                }
                <div className="delete" onClick={handleDelete}>Eliminar</div>
            </div>
        </div>
    );
}

export default Projet;
