import React, { useState, useEffect } from 'react';
import firebase from '../firebase';

const Ajouter: React.FC = () => {
    const [œuvre, setœuvre] = useState({
        auteur: firebase.auth().currentUser?.displayName,
        photoAuteur: firebase.auth().currentUser?.photoURL,
        titre: '',
        photo: ''
    });
    const [percentage, setPercentage] = useState<number>();
    const [photoPrincipale, setPhotoPrincipale] = useState<string>('https://firebasestorage.googleapis.com/v0/b/portarch-4f70e.appspot.com/o/œuvres-photos-principales%2Fgreece.png?alt=media&token=7ef93575-12c5-42c4-aff3-7467e18f019a');
    const [agregado, setAgregado] = useState<string>('');

    useEffect(() => {
        document.getElementsByClassName('lateral')[0].classList.add('ouvert');
    }, []);

    useEffect(() => {
        const mainPictureForm = document.getElementsByClassName('mainPictureForm')[0] as HTMLElement;
        setTimeout(function(){ 
            let width = document.getElementsByClassName('lateral')[0].clientWidth;
            mainPictureForm.style.height = width*0.66 + "px";
            mainPictureForm.style.width = width + "px";  
        }, 500);       
    }, []);

    const handleChangeTitle = (e: React.ChangeEvent<HTMLInputElement>) => {
        const newœuvre = {...œuvre};
        newœuvre.titre = e.target.value;
        setœuvre(newœuvre);
    }

    const handleEditPhoto = () => {
        document.getElementsByName('photo')[0].click()
    }

    const handleChangePhoto = (e: React.ChangeEvent<HTMLInputElement>) => {        
        if(e.target.files) {
            var file = e.target.files[0];
            var ref = firebase.storage().ref().child('œuvres-photos-principales/' + file.name);
            var task = ref.put(file);
            ref.getDownloadURL().then((url) => {
                task.on('state_changed', function(snapshot) {
                    var percentage = (snapshot.bytesTransferred / snapshot.totalBytes) *100;
                    setPercentage(percentage);
                    const newœuvre = {...œuvre};
                    newœuvre.photo = url;
                    setœuvre(newœuvre);
                    if (percentage === 100) {setPhotoPrincipale(url);}
                });
            });
        } else {
            return;
        }
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        firebase.firestore().collection('œuvres').doc().set(œuvre);
        const form = document.getElementsByClassName('addForm')[0] as HTMLFormElement;
        const mainPic = document.getElementsByClassName("mainPictureForm")[0] as HTMLElement;
        form.classList.add('ferme');
        setTimeout(function () {
            form.reset();
            setPercentage(undefined);
            mainPic.style.backgroundImage = 'none';
            setAgregado('AGREGADO !');
        }, 500);
        setTimeout(function () {
            form.classList.remove('ferme');
            setAgregado('');
        }, 3000);
    }

    return (
        <div className="lateral ajouterContainer">
            <h2>AGREGAR</h2>
            <h3>{agregado}</h3>
            <form className="signupForm addForm" onSubmit={(e) => handleSubmit(e)} >
                <label>Título</label>
                <input type="text" onChange={(e) => handleChangeTitle(e)} />
                <label>Foto principal {percentage && Math.round(percentage) + "%"}</label>
                <input style={{display: 'none'}} name="photo" type="file" onChange={(e) => {handleChangePhoto(e)}} />
                <div className="mainPictureForm" style={{backgroundImage: `url(${photoPrincipale})`}}><div className="edit" onClick={handleEditPhoto}>EDITAR</div></div>
                <input type="submit" />
            </form>
        </div>
    );
}

export default Ajouter;
