import React, { useState, useEffect } from 'react';
import './css/panneau.css';
import Cachet from './cachet';
import Album from './album';
import Resume from './resume';
import Auteur from './auteur';

type Props = {
    refresh: boolean,
    setRefresh: any,
    auth: boolean,
    id: string,
    titre: string,
    auteurID: string,
    photo: string,
    description: string,
    mesFavoris?: any
}

const Panneau: React.FC<Props> = ({ refresh, setRefresh, auth, id, titre, auteurID, photo, description, mesFavoris }) => {

    const [portraitWidth, setPortraitWidth] = useState<string>('100vh');
    const [hauteurAuteur, setHauteurAuteur] = useState<string>('0');
    const [enableSwipeListener, setEnableSwipeListener] = useState<boolean>(false);
    const [enablePhotos, setEnablePhotos] = useState<boolean>(false);
    const [enableAuteur, setEnableAuteur] = useState<boolean>(false);
    const [isFavori, setIsFavori] = useState<boolean>(false);

    useEffect(() => {
        document.getElementsByClassName('container')[0].classList.remove('cache');
        if(!document.getElementById(id)?.classList.contains('cache')) {setEnableSwipeListener(true)}
    }, [id]);

    useEffect(() => {
        setIsFavori(false);

        mesFavoris.forEach((favori: any) => {
            if(favori.favori === id) {
                setIsFavori(true);
            }
        });

        if(window.innerWidth <= 600) {
            setPortraitWidth(window.innerWidth + 'px');
            setHauteurAuteur(window.innerWidth + 'px');

            document.getElementsByClassName('panneauContainer')[0].addEventListener('scroll', function() {
                const scrollPosition = document.getElementById(id)?.getBoundingClientRect().top as Number;
                if(scrollPosition > 0 && scrollPosition < 300) {
                    document.getElementById(id)?.classList.remove('cache');
                    setEnableSwipeListener(true);
                } else {
                    document.getElementById(id)?.classList.add('cache');
                    setEnableSwipeListener(false);
                }
            });

            if(enableSwipeListener) {
                document.getElementById(id)?.addEventListener('scroll', function(e) {
                    const swipePositionPhotos = document.getElementById(id + "details")?.getBoundingClientRect().left as Number;
                    const swipePositionAuteur = document.getElementById(id + "Auteur")?.getBoundingClientRect().left as Number;
                    if(swipePositionPhotos < 10) {
                        setEnablePhotos(true)
                    }
                    if(swipePositionAuteur === 0) {
                        setEnableAuteur(true);
                        setHauteurAuteur(window.innerHeight / 1 + 'px');
                    }
                });
            }
        }
    }, [id, enableSwipeListener, mesFavoris]);

    return (
        <div className="container cache" id={id}>
            <div className="panneauFront" style={{backgroundImage: `url(${photo})`, height: `${portraitWidth}`}}>
                <Cachet 
                    refresh={refresh} 
                    setRefresh={setRefresh} 
                    auth={auth} 
                    id={id} 
                    titre={titre} 
                    isFavori={isFavori} 
                />
            </div>
            <div className="panneauBack" id={id + "details"} style={{height: `${portraitWidth}`}}>
                <Album enablePhotos={enablePhotos} œuvreID = {id} />
                <Resume description={description} />
            </div>
            <div className="panneauAuteur" id={id + "Auteur"} style={{height: `${hauteurAuteur}`}}>
                <Auteur enableAuteur={enableAuteur} auteurID={auteurID} portraitWidth={portraitWidth} />
            </div>
        </div>
    );
}

export default Panneau;
