import React, { useEffect } from 'react';
import './css/cachet.css';
import firebase from '../firebase';

type Props = {
    setRefresh: any,
    id: string,
    titre: string,
    auteur: string,
    photoAuteur: string,
    isFavori: boolean
}

const Cachet: React.FC<Props> = ({ setRefresh, id, titre, auteur, photoAuteur, isFavori }) => {

    useEffect(() => {
        if(isFavori) {
            const star = document.getElementById(id + "Star") as HTMLElement;
            star.style.opacity = '1';
        }
    }, [id, isFavori]);

    const addFavorite = () => {
        if(isFavori) {
            return;
        } else if(!isFavori) {
            firebase.firestore().collection('favoris').add({
                favori: id,
                utilisateur: firebase.auth().currentUser?.uid
            });
            setRefresh(true);
            console.log('Favori ajouté !');
        }
    }

    return (
        <div className="cachetContainer">
            <div className="auteur" style={{backgroundImage: `url(${photoAuteur})`}}></div>
            <div className="info">
                <h3>{titre}</h3>
                <span>{auteur}</span>
            </div>
            <div className="star"><div id={id + "Star"} className="starIMG" onClick={addFavorite}></div></div>
        </div>
    );
}

export default Cachet;
