import React, { useState, useEffect } from 'react';
import './css/panneau.css';
import Cachet from './cachet';
import Album from './album';
import Resume from './resume';
import Descriptif from './descriptif';
import isMobile from '../helpers/isMobile';

type Props = {
    id: string,
    titre: string,
    auteur: string,
    photoAuteur: string,
    photo: string,
    photos?: string[]
}

const Panneau: React.FC<Props> = ({ id, titre, auteur, photoAuteur, photo, photos }) => {

    const [descOpened, setDescOpened] = useState<boolean>(false);
    const [portraitWidth, setPortraitWidth] = useState<string>('100vh');

    document.getElementsByClassName('container')[0].remove('cache');

    document.getElementsByClassName('panneauContainer')[0].addEventListener('scroll', function() {
        const scrollPosition = document.getElementById(id)?.getBoundingClientRect().top as Number;
        if(scrollPosition > 0 && scrollPosition < 300) {
            document.getElementById(id)?.classList.remove('cache');
        } else {
            document.getElementById(id)?.classList.add('cache');
        }
    });

    useEffect(() => {
        if(window.innerWidth <= 600) {
            setPortraitWidth(window.innerWidth + 'px');
        }
    }, []);


    const clickDesc = (descOpened: boolean) => {
        if (isMobile()) {
            document.getElementsByClassName('descriptifContainer')[0].classList.toggle('ouvert');
            if (!descOpened) {
                setTimeout(function() {
                    document.getElementsByClassName('albumContainer')[0].classList.toggle('ferme');
                }, 500);
            } else {
                document.getElementsByClassName('albumContainer')[0].classList.toggle('ferme');
            }
            setDescOpened(!descOpened);
        }
    }

    return (
        <div className="container cache" id={id}>
            <div className="panneauFront" style={{backgroundImage: `url(${photo})`, height: `${portraitWidth}`}}>
                <Cachet titre={titre} auteur={auteur} photoAuteur={photoAuteur} />
            </div>
            <div className="panneauBack" style={{height: `${portraitWidth}`}}>
                <div onClick={() => clickDesc(descOpened)}><Resume /></div>
                <Album photos={photos} />
                <Descriptif />
            </div>
        </div>
    );
}

export default Panneau;
