import React, { useState, useEffect } from 'react';
import firebase from '../firebase';
import tinify from '../tinify';

type Props = {
    username: string|null|undefined,
    profilePicture: string|null|undefined,
    setUsername: any,
    setProfilePicture: any
}

const Config: React.FC<Props> = ({ username, setUsername, profilePicture, setProfilePicture }) => {
    const [name, setName] = useState<string>('');
    const [usernameDisplayed, setUsernameDisplayed] = useState<string|null|undefined>('');
    const [editName, setEditName] = useState<boolean>(false);
    const [errorMsg, setErrorMsg] = useState<string>('Elegí un nombre');
    const [errorMsgImg, setErrorMsgImg] = useState<string|undefined>();

    useEffect(() => {
        if (username) {setName(username)}
        setUsernameDisplayed(username);
    }, [username]);

    useEffect(() => {
        const profilePictureDiv = document.getElementsByClassName('profilePictureGrand')[0] as HTMLElement;
        setTimeout(function(){ 
            let width = document.getElementsByClassName('lateral')[0].clientWidth;
            profilePictureDiv.style.height = width + "px";
            profilePictureDiv.style.width = width + "px";  
        }, 500);       
    }, []);

    const signOut = () => {
        firebase.auth().signOut();
        window.location.reload(true);
    }

    const handleChangeName = (e: React.ChangeEvent<HTMLInputElement>) => {
        setName(e.target.value);
    }

    const handleSubmitName = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if(name.length < 2) {
            setName('');
            setErrorMsg('Debe tener al menos 2 carácteres !');
            setTimeout(function(){ setErrorMsg('Elegí un nombre'); }, 3000);
            return;
        }
        var user = firebase.auth().currentUser;
        if (user) {
            user.updateProfile({
                displayName: name,
            });
            setEditName(false);
            setUsername(name);
        } else {
          return;
        }
    }

    const handleEditPhoto = () => {
        document.getElementsByName('photo')[0].click()
    }

    const handleChangePhoto = (e: React.ChangeEvent<HTMLInputElement>) => {
        var user = firebase.auth().currentUser;
        setErrorMsgImg(undefined);

        if(e.target.files && user) {
            var file = e.target.files[0];
            if(file.type !== 'image/jpeg' && file.type !== 'image/png') {
                setErrorMsgImg('Debe ser JPG o PNG !'); 
                return;
            }
            // tinify.fromFile(file.name).toFile("OPT_" + file.name); 
            var ref = firebase.storage().ref().child('profilePicture/' + user.uid);
            var task = ref.put(file);
            ref.getDownloadURL().then((url) => {
                user?.updateProfile({
                    photoURL: url
                });
                task.on('state_changed', function(snapshot) {
                    var percentage = (snapshot.bytesTransferred / snapshot.totalBytes) *100;
                    if (percentage === 100) {setProfilePicture(url);}
                });
            });
        } else {
            return;
        }
    }

    return (
        <div className="configContainer">
            {
                (username === null || editName === true) &&
                <form className="signupForm" onSubmit={(e) => {handleSubmitName(e)}}>
                    <input autoFocus name="username" type="text" autoComplete="off" className="editNameInput" placeholder={errorMsg} value={name} onChange={(e) => {handleChangeName(e)}}/>
                </form>
            }
            {
                (username!== null && editName === false) &&
                <h3 className="username">{usernameDisplayed}<div className="edit" onClick={() => setEditName(true)}></div></h3>
            }
            <div className="profilePictureGrand" style={{backgroundImage: `url(${profilePicture})`}}><div className="error">{errorMsgImg}</div><div className="edit" onClick={handleEditPhoto}>EDITAR</div></div>
                <form className="signupForm" style={{display: 'none'}}>
                    <input name="photo" type="file" onChange={(e) => {handleChangePhoto(e)}}/>
                </form>
            <button onClick={signOut}>Desconectarse</button>
        </div>
    );
}

export default Config;
