import React, { useState, useEffect } from 'react';
import './css/menu.css';
import firebase from './../firebase';

function UseTendances() {
    const [œuvres, setœuvres] = useState<any>([]);

    useEffect(() => {
        firebase.firestore().collection('œuvres').orderBy('note', 'desc').limit(5).onSnapshot((snapshot) => {
            const newœuvres = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data(),
                photos: [] as any
            }));

            setœuvres(newœuvres);
        });
    }, []);

    return œuvres;
}

type Props = {
    titre?: string,
    img?: string
}

const Tendance: React.FC<Props> = ({ titre, img }) => {

    const tendances = UseTendances();

    return(
        <div className='tendanceContainer' style={{backgroundImage: `url(${img})`}}>
            <div className="tendanceCover">
                <h4>{titre}</h4>
            </div>
        </div>
    );
}

export default Tendance;
