import React, { useState, useEffect } from 'react';
import firebase from '../firebase';

type Props = {
    setRefresh: any,
    id: string,
    titre?: string,
    photo?: string
}

const Projet: React.FC<Props> = ({ setRefresh, id, titre, photo }) => {
    const [editTitre, setEditTitre] = useState<boolean>(false);
    const [editedTitre, setEditedTitre] = useState<string>();

    useEffect(() => {
        document.getElementById(id)?.classList.add('ouvert');
        setEditedTitre(titre);
    }, [id, titre]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setEditedTitre(e.target.value);
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        firebase.firestore().collection('œuvres').doc(id).update({
            titre: editedTitre
        });
        setEditTitre(false);
        setRefresh(true);
    }

    const handleDelete = () => {
        firebase.firestore().collection('œuvres').doc(id).delete();
    }

    return(
        <div className="projet" id={id} style={{backgroundImage: `url(${photo})`}}>
            <div className="edit">
                {editTitre === false && <h4 onClick={() => setEditTitre(true)}>{titre}</h4>}
                {editTitre === true && 
                    <form onSubmit={(e) => handleSubmit(e)}>
                        <input autoFocus type="text" value={editedTitre} onChange={(e) => handleChange(e)} />
                    </form>
                }
                <div className="delete" onClick={handleDelete}>Eliminar</div>
            </div>
        </div>
    );
}

export default Projet;
