import React, { useState, useEffect } from 'react';
import Tendance from './tendance';
import firebase from '../firebase';

function useTendances() {
    const [tendances, setTendances] = useState<any>([]);

    useEffect(() => {
        firebase.firestore().collection('tendences').orderBy('classement', 'asc').onSnapshot((snapshot) => {
            const newTendance = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }));

            setTendances(newTendance);
        });
    }, []);

    return tendances;
}

const Recherche: React.FC = () => {
    const tendances = useTendances();

    useEffect(() => {
        document.getElementsByClassName('lateral')[0].classList.add('ouvert');
    }, []);

    return (
        <div className="lateral rechercheContainer">
            <h2><input type="text" placeholder="Palabras claves..."></input>BUSCAR</h2>
            <h3>TENDENCIAS</h3>
            {Object.keys(tendances).length === 0 && <div><Tendance /><Tendance /><Tendance /><Tendance /><Tendance /></div>}
            {tendances.map((tendance: any) => <Tendance key={tendance.id} titre={tendance.titre} img={tendance.img} />)}
        </div>
    );
}

export default Recherche;
