import React, { useState, useEffect } from 'react';
import './css/album.css';
import firebase from '../firebase';

type Props = {
    enablePhotos: boolean,
    œuvreID: string
}

const Album: React.FC<Props> = ({ enablePhotos, œuvreID }) => {

    function usePhotos() {
        const [photos, setPhotos] = useState<any>([]);

        useEffect(() => {
            firebase.firestore().collection('photos').where('œuvre', '==', œuvreID).onSnapshot((snapshot) => {
                const newPhotos = snapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data()
                }));

                setPhotos(newPhotos);
            });
        }, []);

        return photos;
    }

    return (
        <div className="albumContainer">
            {/* photos.map((photo) => 
                <div key={photo} className="photoContainer" style={{backgroundImage: `url(${photo})`}}></div>
            ) */}
            <div className="photoContainer"></div>
            <div className="photoContainer"></div>
            <div className="photoContainer"></div>
            <div className="photoContainer"></div>
            <div className="photoContainer"></div>
            <div className="photoContainer"></div>
        </div>
    );
}

export default Album;
