import React, { useState, useEffect } from 'react';
import Panneau from './components/panneau'
import Menu from './components/menu';
import firebase from './firebase';
import Hamburger from './components/hamburger';

function Useœuvres() {
    const [œuvres, setœuvres] = useState<any>([]);

    useEffect(() => {
        firebase.firestore().collection('œuvres').onSnapshot((snapshot) => {
            const newœuvres = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data(),
                photos: [] as any
            }));

            setœuvres(newœuvres);
        });
    }, []);

    return œuvres;
}

function usePhotos() {
    const [photos, setPhotos] = useState<any>([]);

    useEffect(() => {
        firebase.firestore().collection('photos').onSnapshot((snapshot) => {
            const newPhotos = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }));

            setPhotos(newPhotos);
        });
    }, []);

    return photos;
}

function UseMesœuvres() {
    const [mesœuvres, setMesœuvres] = useState<any>([]);

    useEffect(() => {
        firebase.firestore().collection('œuvres').where('auteurID', '==', firebase.auth().currentUser?.uid).onSnapshot((snapshot) => {
            const newMesœuvres = snapshot.docs.map((doc) => ({
                id: doc.id,
                ...doc.data()
            }));

            setMesœuvres(newMesœuvres);
        });
    }, []);
}

const App: React.FC = () => {

    const [auth, setAuth] = useState<boolean>(false);
    const [username, setUsername] = useState<string|null|undefined>();
    const [profilePicture, setProfilePicture] = useState<string|null|undefined>();
    const œuvres = Useœuvres();
    const photos = usePhotos();
    const mesœuvres = UseMesœuvres();

    œuvres.forEach((œuvre: any) => {
        photos.forEach((photo: any) => {
            if(photo.œuvre === œuvre.id && !Object.values(œuvre.photos).includes(photo.url)) {
                œuvre.photos.push(photo.url);
            }
        })
    });

    useEffect(() => {
        firebase.auth().onAuthStateChanged(function(user) {
            if (user) {
              // User is signed in.
              setAuth(true);
              setUsername(user.displayName);
              user.photoURL ? setProfilePicture(user.photoURL) : setProfilePicture('https://firebasestorage.googleapis.com/v0/b/portarch-4f70e.appspot.com/o/icônes%2Fprofil2.png?alt=media&token=0f86a5d5-f039-42af-89fc-4f915f10a3d5');
            } else {
              // User is signed out.
              setAuth(false)
            }
        });
    }, []);

    const reload = () => {
        window.location.reload(true);
    }

    /* useEffect(() => {
        œuvres.forEach((œuvre: any) => {
            photos.forEach((photo: any) => {
                if(photo.œuvre === œuvre.id) {
                    œuvre.photos.push(photo.url);
                }
            })
        });
    }, [œuvres, photos]); */

 return (
  <div>
      <h1 onClick={reload}>PORTARCH</h1>
      <Hamburger />
      <Menu auth={auth} username={username} setUsername={setUsername} profilePicture={profilePicture} setProfilePicture={setProfilePicture} />
      <div className="panneauContainer">
        {œuvres.map((œuvre: any) => <Panneau key={œuvre.id} titre={œuvre.titre} auteur={œuvre.auteur} photoAuteur={œuvre.photoAuteur} photo={œuvre.photo} photos={œuvre.photos} />)}
      </div>
  </div>
 )
}

export default App;
