import React, { useState, useEffect } from 'react';
import './css/cachet.css';
import firebase from '../firebase';

type Props = {
    refresh: boolean,
    setRefresh: any,
    auth: boolean,
    id: string,
    titre: string,
    auteur: string,
    photoAuteur: string,
    isFavori: boolean
}

const Cachet: React.FC<Props> = ({ refresh, setRefresh, id, titre, auteur, photoAuteur, isFavori }) => {

    const userID = firebase.auth().currentUser?.uid;
    const [note, setNote] = useState<number>(0);

    useEffect(() => {
        const star = document.getElementById(id + "Star") as HTMLElement;
        star.style.opacity = '0.25';
        if(isFavori) {
            star.style.opacity = '1';
        }
        firebase.firestore().collection('œuvres').doc(id).onSnapshot((snapshot) => {
            setNote(snapshot.data()?.note);
        });
    }, [id, isFavori]);

    const addFavorite = () => {
        if(isFavori) {
            firebase.firestore().collection('favoris').doc(userID+id).delete();
            setRefresh(!refresh);
            console.log('Favori supprimé !');
            firebase.firestore().collection('œuvres').doc(id).update({
                note: note-1
            });
            return;
        } else if(!isFavori) {
            firebase.firestore().collection('favoris').doc(userID+id).set({
                favori: id,
                utilisateur: userID
            });
            setRefresh(!refresh);
            console.log('Favori ajouté !');
            firebase.firestore().collection('œuvres').doc(id).update({
                note: note+1
            });
        }
    }

    return (
        <div className="cachetContainer">
            <div className="auteur" style={{backgroundImage: `url(${photoAuteur})`}}></div>
            <div className="info">
                <h3>{titre}</h3>
                <span>{auteur}</span>
            </div>
            <div className="star"><div id={id + "Star"} className="starIMG" onClick={addFavorite}></div></div>
        </div>
    );
}

export default Cachet;
