import React, { useState, useEffect } from 'react';
import './css/auteur.css';
import firebase from './../firebase';

type Props = {
    enableAuteur: boolean,
    auteurID: string,
    auteur: string,
    photoAuteur: string,
    portraitWidth: string
}

const Auteur: React.FC<Props> = ({ enableAuteur, auteurID, auteur, photoAuteur, portraitWidth }) => {

    function UseAuteur() {
        const [auteurObject, setAuteurObject] = useState<any>([]);

        useEffect(() => {
            if(enableAuteur) {
                firebase.firestore().collection('config').doc(auteurID).onSnapshot((snapshot) => {
                    setAuteurObject(snapshot.data());
                });
            }

        // eslint-disable-next-line react-hooks/exhaustive-deps
        }, [enableAuteur]);

        return auteurObject;
    }

    function UseSesœuvres() {
        const [sesœuvres, setSesœuvres] = useState<any>([]);

        useEffect(() => {
            if(enableAuteur) {
                firebase.firestore().collection('œuvres').where('auteurID', '==', auteurID).onSnapshot((snapshot) => {
                    const newSesœuvres = snapshot.docs.map((doc) => ({
                        id: doc.id,
                        ...doc.data()
                    }));

                    setSesœuvres(newSesœuvres);
                });
            }         
        // eslint-disable-next-line react-hooks/exhaustive-deps
        }, [enableAuteur]);

        return sesœuvres;
    }

    const sesœuvres = UseSesœuvres();
    const auteurObject = UseAuteur();
    console.log(auteurObject);

    return(
        <div className="auteurContainer">
            <div className="infoAuteur">
                <div className="auteurPhoto" style={{backgroundImage: `url(${photoAuteur})`}}></div>
                <div><h2>{auteur}</h2></div>
            </div>
            {enableAuteur && sesœuvres.map((œuvre: any) => <div key={œuvre.id + 'portfolio'} className="portfolioContainer" style={{backgroundImage: `url(${œuvre.photo})`, height: `${portraitWidth}`}}></div>)}
        </div>
    );
}

export default Auteur;
