import React, { useState, useEffect } from 'react';
import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';

type Props = {
    setRefresh: any,
    id: string,
    titre?: string,
    photo?: string,
    description?: string
}

const Projet: React.FC<Props> = ({ setRefresh, id, titre, photo, description }) => {
    const [enableEdit, setEnableEdit] = useState<boolean>(false);
    const [editTitre, setEditTitre] = useState<boolean>(false);
    const [editedTitre, setEditedTitre] = useState<string>();
    const [editDescription, setEditDescription] = useState<boolean>(false);
    const [confirmDelete, setConfirmDelete] = useState<boolean>(false);
    const [width, setWidth] = useState<number>(0);

    useEffect(() => {
        const projet = document.getElementById('mon' + id) as HTMLElement;
        setEditedTitre(titre);
        setWidth(document.getElementsByClassName('lateral')[0].clientWidth);
        setTimeout(function() {
            projet.style.height = width + 'px';
            projet.style.width = width + 'px';
        }, 500);
    }, [id, titre, width]);

    const handleEdit = (e: React.MouseEvent<HTMLElement>) => {
        if(!enableEdit)
        {
            console.log('click');
            e.currentTarget.querySelector('.edit')?.classList.add('ouvert');
            setEnableEdit(true);
        }
    }

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setEditedTitre(e.target.value);
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        firebase.firestore().collection('œuvres').doc(id).update({
            titre: editedTitre
        });
        setEditTitre(false);
        setRefresh(true);
    }

    const handleDelete = () => {
        firebase.firestore().collection('œuvres').doc(id).delete();
    }

    const handleFinishEdit = (e: React.MouseEvent<HTMLElement>) => {
        e.currentTarget.parentElement?.classList.remove('ouvert');
        setEnableEdit(false);
    }

    return(
        <div className="projet" id={'mon' + id} style={{backgroundImage: `url(${photo})`}} onClick={(e) => handleEdit(e)}>
            <div className="edit">
                {editTitre === false && <h4 onClick={() => setEditTitre(true)}>{titre}</h4>}
                {editTitre === true && 
                    <form className="signupForm" onSubmit={(e) => handleSubmit(e)}>
                        <input autoFocus type="text" value={editedTitre} onChange={(e) => handleChange(e)} />
                    </form>
                }
                {editDescription === false && <div className="description">{description || "Sin descripción"}</div>}
                {editDescription === true && <textarea rows={4} maxLength={140}></textarea>}
                <div className="ok" onClick={(e) => handleFinishEdit(e)}>Terminar</div>
                {confirmDelete === false && <div className="delete" onClick={() => setConfirmDelete(true)}>Eliminar</div>}
                {confirmDelete === true && <div className="confirmDelete">Queres eliminar este proyecto?<br /><span onClick={handleDelete}>SI</span><span onClick={() => setConfirmDelete(false)}>NO</span></div>}
            </div>
        </div>
    );
}

export default Projet;
