import React from 'react';
import './css/auteur.css';
import firebase from './../firebase';

type Props = {
    enableAuteur: boolean,
    auteur: string,
    photoAuteur: string,
    portraitWidth: string
}

const Auteur: React.FC<Props> = ({ enableAuteur, auteur, photoAuteur, portraitWidth }) => {
    const monArray: any = [
        0,
        1,
        2
    ];
    return(
        <div className="auteurContainer">
            <div className="infoAuteur">
                <div className="auteurPhoto" style={{backgroundImage: `url(${photoAuteur})`}}></div>
                <div><h2>{auteur}</h2></div>
            </div>
            {enableAuteur && monArray.map((item: any) => <div className="portfolioContainer" style={{height: `${portraitWidth}`}}></div>)}
        </div>
    );
}

export default Auteur;
