import React, { useState, useEffect } from 'react';
import './css/menu.css';

type Props = {
    titre?: string,
    photo?: string
}

const Tendance: React.FC<Props> = ({ titre, photo }) => {

    const [width, setWidth] = useState<string>('');

    useEffect(() => {
        setWidth(document.getElementsByClassName('lateral')[0].clientWidth + 'px');
    }, []);

    return(
        <div className='tendanceContainer' style={{backgroundImage: `url(${photo})`, height: width}}>
            <div className="tendanceCover">
                <h4>{titre}</h4>
            </div>
        </div>
    );
}

export default Tendance;
