import React, { useState, useEffect } from 'react';
import firebase from '../firebase';

const Ajouter: React.FC = () => {
    const [œuvre, setœuvre] = useState({
        auteur: firebase.auth().currentUser?.displayName,
        photoAuteur: firebase.auth().currentUser?.photoURL,
        titre: '',
        photoPrincipale: ''
    });
    const [percentage, setPercentage] = useState<number>();

    useEffect(() => {
        document.getElementsByClassName('lateral')[0].classList.add('ouvert');
    }, []);

    useEffect(() => {
        const mainPictureForm = document.getElementsByClassName('mainPictureForm')[0] as HTMLElement;
        setTimeout(function(){ 
            let width = document.getElementsByClassName('lateral')[0].clientWidth;
            mainPictureForm.style.height = width/2 + "px";
            mainPictureForm.style.width = width + "px";  
        }, 500);       
    }, []);

    const handleEditPhoto = () => {
        document.getElementsByName('photo')[0].click()
    }

    const handleChangePhoto = (e: React.ChangeEvent<HTMLInputElement>) => {        
        if(e.target.files) {
            var file = e.target.files[0];
            var ref = firebase.storage().ref().child('œuvres-photos-principales/' + file.name);
            var task = ref.put(file);
            ref.getDownloadURL().then((url) => {
                task.on('state_changed', function(snapshot) {
                    var percentage = (snapshot.bytesTransferred / snapshot.totalBytes) *100;
                    setPercentage(percentage);
                    const newœuvre = {...œuvre};
                    newœuvre.photoPrincipale = url;
                    setœuvre(newœuvre);
                });
            });
        } else {
            return;
        }
    }

    return (
        <div className="lateral ajouterContainer">
            <h2>AGREGAR</h2>
            <form className="signupForm">
                <label>Título</label>
                <input type="text" />
                <label>Foto principal {percentage}</label>
                <input style={{display: 'none'}} name="photo" type="file" onChange={(e) => {handleChangePhoto(e)}} />
                <div className="mainPictureForm"><div className="edit" onClick={handleEditPhoto}>EDITAR</div></div>
            </form>
        </div>
    );
}

export default Ajouter;
