import React, { useState, useEffect } from 'react';
import firebase from '../firebase';
import Projet from './projet';

type Props = {
    setRefresh: any,
    mesœuvres: any
}

const Ajouter: React.FC<Props> = ({ setRefresh, mesœuvres}) => {
    const [œuvre, setœuvre] = useState({
        auteurID: firebase.auth().currentUser?.uid,
        auteur: firebase.auth().currentUser?.displayName?.toLowerCase(),
        note: 0,
        photoAuteur: firebase.auth().currentUser?.photoURL ? firebase.auth().currentUser?.photoURL : 'https://firebasestorage.googleapis.com/v0/b/portarch-4f70e.appspot.com/o/icônes%2Fprofil2.png?alt=media&token=8342524c-b192-45be-83c3-86a0fb11bf37',
        titre: '',
        photo: ''
    });
    const [randomId] = useState<string>(Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15));
    const [percentage, setPercentage] = useState<number>();
    const [photoPrincipale, setPhotoPrincipale] = useState<string>('https://firebasestorage.googleapis.com/v0/b/portarch-4f70e.appspot.com/o/œuvres-photos-principales%2Fgreece.png?alt=media&token=75893cf5-6875-4b7c-aca5-9ba03f71cb01');
    const [photosSecondaires, setPhotosSecondaires] = useState<any>([]);
    const [agregado, setAgregado] = useState<string>('');
    const [width, setWidth] = useState<number>(0);

    useEffect(() => {
        document.getElementsByClassName('lateral')[0].classList.add('ouvert');
        setWidth(document.getElementsByClassName('lateral')[0].clientWidth);
    }, []);

    useEffect(() => {
        if(mesœuvres.length < 1) {
            setAgregado('AGREGAR'); 
            document.getElementsByClassName('addForm')[0].classList.remove('ferme');
        }
        else {
            setTimeout(function(){ 
                setAgregado('AGREGAR');
                document.getElementsByClassName('addForm')[0].classList.remove('ferme');
            }, 500); 
        }      
    }, [mesœuvres.length]);

    const handleChangeTitle = (e: React.ChangeEvent<HTMLInputElement>) => {
        const newœuvre = {...œuvre};
        newœuvre.titre = e.target.value.toLowerCase();
        setœuvre(newœuvre);
    }

    const handleEditPhoto = () => {
        document.getElementsByName('photo')[0].click()
    }

    const handleEditPhotos = () => {
        document.getElementsByName('photo2')[0].click();
    }

    const handleChangePhoto = (e: React.ChangeEvent<HTMLInputElement>) => {        
        if(e.target.files) {
            var file = e.target.files[0];
            if(file.type !== 'image/jpeg' && file.type !== 'image/png') {
                return;
            }
            var ref = firebase.storage().ref('œuvres-photos-principales/' + file.name);
            var task = ref.put(file);
            task.on('state_changed', function(snapshot) {
                var percentage = (snapshot.bytesTransferred / snapshot.totalBytes) *100;
                setPercentage(percentage);

                if(percentage === 100) {
                    setTimeout(function() {
                        ref.getDownloadURL().then((url) => {
                            setPhotoPrincipale(url);
                            const newœuvre = {...œuvre};
                            newœuvre.photo = url;
                            setœuvre(newœuvre);
                        });
                    }, 1000);
                }
            });
        } else {
            return;
        }
    }

    const handleChangePhotos = (e: React.ChangeEvent<HTMLInputElement>) => {    
        if(e.target.files) {
            var tempo: any = [];
            var files = Array.from(e.target.files);
            console.log(files);
            files.forEach((file) => {
                if(file.type !== 'image/jpeg' && file.type !== 'image/png') {
                    return;
                }
                var ref = firebase.storage().ref('œuvres-photos-secondaires/' + file.name);
                ref.put(file).then((result) => {
                    ref.getDownloadURL().then((url) => {
                        firebase.firestore().collection('photos').add({
                            url: url,
                            œuvre: randomId
                        });
                        tempo.push(url);
                    });
                });
            });
            console.log(tempo);
        } else {
            return;
        }
    }

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        firebase.firestore().collection('œuvres').doc(randomId).set(œuvre);
        const form = document.getElementsByClassName('addForm')[0] as HTMLFormElement;
        const mainPic = document.getElementsByClassName("mainPictureForm")[0] as HTMLElement;
        setTimeout(function () {
            form.reset();
            form.classList.add('ferme');
            setPercentage(undefined);
            mainPic.style.backgroundImage = 'none';
            setAgregado('AGREGADO !');
        }, 500);
        setTimeout(function () {
            setAgregado('AGREGAR');
            form.classList.remove('ferme');
            mainPic.style.backgroundImage = 'url("https://firebasestorage.googleapis.com/v0/b/portarch-4f70e.appspot.com/o/œuvres-photos-principales%2Fgreece.png?alt=media&token=75893cf5-6875-4b7c-aca5-9ba03f71cb01")';
        }, 3000);
    }

    return (
        <div className="lateral ajouterContainer">
            <h2>MIS PROYECTOS</h2>
            <div className="œuvresContainer">
                {mesœuvres.map((œuvre: any) => <Projet 
                                                    setRefresh={setRefresh}
                                                    key={œuvre.id} 
                                                    id={œuvre.id} 
                                                    titre={œuvre.titre} 
                                                    photo={œuvre.photo}
                                                />)}
                <br />
                {mesœuvres.length < 3 &&
                    <div>
                        <h3>{agregado}</h3>
                        <form className="signupForm addForm ferme" onSubmit={(e) => handleSubmit(e)} >
                            <label>Título</label>
                            <input type="text" onChange={(e) => handleChangeTitle(e)} />
                            <label>Foto principal {percentage && Math.round(percentage) + "%"}</label>
                            <input style={{display: 'none'}} name="photo" type="file" onChange={(e) => {handleChangePhoto(e)}} />
                            <div className="mainPictureForm" style={{backgroundImage: `url(${photoPrincipale})`, height: width + 'px'}}><div className="edit" onClick={handleEditPhoto}>EDITAR</div></div>
                            <input style={{display: 'none'}} name="photo2" type="file" onChange={(e) => {handleChangePhotos(e)}} multiple />
                            {/* photosSecondaires.map((photoSecondaire: any) => 
                                <div style={{backgroundImage: `url(${photoSecondaire})`,height: width/3.1 + 'px', width: width/3.1 + 'px', marginTop: '10px'}}></div>
                            ) */}
                            <div className="secondaryPicturesForm" style={{height: width/3.1 + 'px', width: width/3.1 + 'px'}} onClick={handleEditPhotos}></div>
                            <input type="submit" />
                        </form>
                    </div>
                }    
            </div>
        </div>
    );
}

export default Ajouter;
